
$(function(){
    $.ajax({url:"../../Sys/Manager/GetCurrentUser",success:function(response){
        if(response.success){
            var userName = response.data.trueName;

            $(".spnCurrentUserName").text("您好，"+userName+" !");
            $(".pCurrentUserName").text(userName);
        }
    }});
    $(".full-page").height($(".content-wrapper").height());

    $("#mgrSignOut").click(function(){
        $.get("../../Sys/Login/SignOut").done(function(response){
            if(response.success){
                window.location.href ="../../Home/Index";
            }
        })

    });
});